        uint64 itemId;
        uint64 number;
    }

    struct Item {
        uint64 totalSupply;
        uint64 limitSupply;
    }

    struct Mint {
        uint64 itemId;
        uint256 tokenId;
    }

    modifier onlyExistToken(uint256 tokenId) {
        require(_exists(tokenId), "MaplestoryEquipSBT/tokenNotExists: token is not exist");
        _;
