        // Note that 0 <= p[1] < FIELD_SIZE
        // so this cannot wrap, we use unchecked to save gas.
        p[1] = FIELD_SIZE - p[1];
      }
    }
  }

  // Domain-separation tag for initial hash in hashToCurve. Corresponds to
  // vrf.go/hashToCurveHashPrefix
  uint256 internal constant HASH_TO_CURVE_HASH_PREFIX = 1;

  // Cryptographic hash function onto the curve.
  //
  // Corresponds to algorithm in section 5.4.1.1 of the draft standard. (But see
  // DESIGN NOTES above for slight differences.)
  //
  // TODO(alx): Implement a bounded-computation hash-to-curve, as described in
  // "Construction of Rational Points on Elliptic Curves over Finite Fields"
  // http://citeseerx.ist.psu.edu/viewdoc/download?doi=10.1.1.831.5299&rep=rep1&type=pdf
  // and suggested by
  // https://datatracker.ietf.org/doc/html/draft-irtf-cfrg-hash-to-curve-01#section-5.2.2
  // (Though we can't used exactly that because secp256k1's j-invariant is 0.)
  //
  // This would greatly simplify the analysis in "OTHER SECURITY CONSIDERATIONS"
  // https://www.pivotaltracker.com/story/show/171120900
  function hashToCurve(uint256[2] memory pk, uint256 input) internal view returns (uint256[2] memory rv) {
    rv = newCandidateSecp256k1Point(abi.encodePacked(HASH_TO_CURVE_HASH_PREFIX, pk, input));
    while (!isOnCurve(rv)) {
      rv = newCandidateSecp256k1Point(abi.encodePacked(rv[0]));
    }
  }

  /** *********************************************************************
   * @notice Check that product==scalar*multiplicand
   *
   * @dev Based on Vitalik Buterin's idea in ethresear.ch post cited below.
   *
   * @param multiplicand: secp256k1 point
   * @param scalar: non-zero GF(GROUP_ORDER) scalar
   * @param product: secp256k1 expected to be multiplier * multiplicand
   * @return verifies true iff product==scalar*multiplicand, with cryptographically high probability
   */
  function ecmulVerify(
    uint256[2] memory multiplicand,
    uint256 scalar,
    uint256[2] memory product
  ) internal pure returns (bool verifies) {
    require(scalar != 0, "zero scalar"); // Rules out an ecrecover failure case
    uint256 x = multiplicand[0]; // x ordinate of multiplicand
    uint8 v = multiplicand[1] % 2 == 0 ? 27 : 28; // parity of y ordinate
    // https://ethresear.ch/t/you-can-kinda-abuse-ecrecover-to-do-ecmul-in-secp256k1-today/2384/9
    // Point corresponding to address ecrecover(0, v, x, s=scalar*x) is
    // (x‚Åª¬π mod GROUP_ORDER) * (scalar * x * multiplicand - 0 * g), i.e.
    // scalar*multiplicand. See https://crypto.stackexchange.com/a/18106
    bytes32 scalarTimesX = bytes32(mulmod(scalar, x, GROUP_ORDER));
    address actual = ecrecover(bytes32(0), v, bytes32(x), scalarTimesX);
    // Explicit conversion to address takes bottom 160 bits
    address expected = address(uint160(uint256(keccak256(abi.encodePacked(product)))));
    return (actual == expected);
  }

  // Returns x1/z1-x2/z2=(x1z2-x2z1)/(z1z2) in projective coordinates on P¬π(ùîΩ‚Çô)
  function projectiveSub(
    uint256 x1,
    uint256 z1,
    uint256 x2,
    uint256 z2
  ) internal pure returns (uint256 x3, uint256 z3) {
    unchecked {
      uint256 num1 = mulmod(z2, x1, FIELD_SIZE);
      // Note this cannot wrap since x2 is a point in [0, FIELD_SIZE-1]
      // we use unchecked to save gas.
      uint256 num2 = mulmod(FIELD_SIZE - x2, z1, FIELD_SIZE);
      (x3, z3) = (addmod(num1, num2, FIELD_SIZE), mulmod(z1, z2, FIELD_SIZE));
    }
  }

  // Returns x1/z1*x2/z2=(x1x2)/(z1z2), in projective coordinates on P¬π(ùîΩ‚Çô)
  function projectiveMul(
    uint256 x1,
    uint256 z1,
    uint256 x2,
    uint256 z2
  ) internal pure returns (uint256 x3, uint256 z3) {
    (x3, z3) = (mulmod(x1, x2, FIELD_SIZE), mulmod(z1, z2, FIELD_SIZE));
  }

  /** **************************************************************************
        @notice Computes elliptic-curve sum, in projective co-ordinates

        @dev Using projective coordinates avoids costly divisions

        @dev To use this with p and q in affine coordinates, call
        @dev projectiveECAdd(px, py, qx, qy). This will return
        @dev the addition of (px, py, 1) and (qx, qy, 1), in the
        @dev secp256k1 group.

        @dev This can be used to calculate the z which is the inverse to zInv
        @dev in isValidVRFOutput. But consider using a faster
        @dev re-implementation such as ProjectiveECAdd in the golang vrf package.

        @dev This function assumes [px,py,1],[qx,qy,1] are valid projective
             coordinates of secp256k1 points. That is safe in this contract,
             because this method is only used by linearCombination, which checks
             points are on the curve via ecrecover.
        **************************************************************************
        @param px The first affine coordinate of the first summand
        @param py The second affine coordinate of the first summand
        @param qx The first affine coordinate of the second summand
        @param qy The second affine coordinate of the second summand

        (px,py) and (qx,qy) must be distinct, valid secp256k1 points.
        **************************************************************************
        Return values are projective coordinates of [px,py,1]+[qx,qy,1] as points
        on secp256k1, in P¬≤(ùîΩ‚Çô)
        @return sx
        @return sy
        @return sz
    */
  function projectiveECAdd(
    uint256 px,
    uint256 py,
    uint256 qx,
    uint256 qy
  ) internal pure returns (uint256 sx, uint256 sy, uint256 sz) {
    unchecked {
      // See "Group law for E/K : y^2 = x^3 + ax + b", in section 3.1.2, p. 80,
      // "Guide to Elliptic Curve Cryptography" by Hankerson, Menezes and Vanstone
      // We take the equations there for (sx,sy), and homogenize them to
      // projective coordinates. That way, no inverses are required, here, and we
      // only need the one inverse in affineECAdd.

      // We only need the "point addition" equations from Hankerson et al. Can
      // skip the "point doubling" equations because p1 == p2 is cryptographically
      // impossible, and required not to be the case in linearCombination.

      // Add extra "projective coordinate" to the two points
      (uint256 z1, uint256 z2) = (1, 1);

      // (lx, lz) = (qy-py)/(qx-px), i.e., gradient of secant line.
      // Cannot wrap since px and py are in [0, FIELD_SIZE-1]
      uint256 lx = addmod(qy, FIELD_SIZE - py, FIELD_SIZE);
      uint256 lz = addmod(qx, FIELD_SIZE - px, FIELD_SIZE);

      uint256 dx; // Accumulates denominator from sx calculation
      // sx=((qy-py)/(qx-px))^2-px-qx
      (sx, dx) = projectiveMul(lx, lz, lx, lz); // ((qy-py)/(qx-px))^2
      (sx, dx) = projectiveSub(sx, dx, px, z1); // ((qy-py)/(qx-px))^2-px
      (sx, dx) = projectiveSub(sx, dx, qx, z2); // ((qy-py)/(qx-px))^2-px-qx

      uint256 dy; // Accumulates denominator from sy calculation
      // sy=((qy-py)/(qx-px))(px-sx)-py
      (sy, dy) = projectiveSub(px, z1, sx, dx); // px-sx
      (sy, dy) = projectiveMul(sy, dy, lx, lz); // ((qy-py)/(qx-px))(px-sx)
      (sy, dy) = projectiveSub(sy, dy, py, z1); // ((qy-py)/(qx-px))(px-sx)-py

      if (dx != dy) {
        // Cross-multiply to put everything over a common denominator
        sx = mulmod(sx, dy, FIELD_SIZE);
        sy = mulmod(sy, dx, FIELD_SIZE);
        sz = mulmod(dx, dy, FIELD_SIZE);
      } else {
        // Already over a common denominator, use that for z ordinate
        sz = dx;
      }
    }
  }

  // p1+p2, as affine points on secp256k1.
  //
  // invZ must be the inverse of the z returned by projectiveECAdd(p1, p2).
  // It is computed off-chain to save gas.
  //
  // p1 and p2 must be distinct, because projectiveECAdd doesn't handle
  // point doubling.
  function affineECAdd(
    uint256[2] memory p1,
    uint256[2] memory p2,
    uint256 invZ
  ) internal pure returns (uint256[2] memory) {
    uint256 x;
    uint256 y;
    uint256 z;
    (x, y, z) = projectiveECAdd(p1[0], p1[1], p2[0], p2[1]);
    require(mulmod(z, invZ, FIELD_SIZE) == 1, "invZ must be inverse of z");
    // Clear the z ordinate of the projective representation by dividing through
    // by it, to obtain the affine representation
    return [mulmod(x, invZ, FIELD_SIZE), mulmod(y, invZ, FIELD_SIZE)];
  }

  // True iff address(c*p+s*g) == lcWitness, where g is generator. (With
  // cryptographically high probability.)
  function verifyLinearCombinationWithGenerator(
    uint256 c,
    uint256[2] memory p,
    uint256 s,
    address lcWitness
  ) internal pure returns (bool) {
    // Rule out ecrecover failure modes which return address 0.
    unchecked {
      require(lcWitness != address(0), "bad witness");
      uint8 v = (p[1] % 2 == 0) ? 27 : 28; // parity of y-ordinate of p
      // Note this cannot wrap (X - Y % X), but we use unchecked to save
      // gas.
      bytes32 pseudoHash = bytes32(GROUP_ORDER - mulmod(p[0], s, GROUP_ORDER)); // -s*p[0]
      bytes32 pseudoSignature = bytes32(mulmod(c, p[0], GROUP_ORDER)); // c*p[0]
      // https://ethresear.ch/t/you-can-kinda-abuse-ecrecover-to-do-ecmul-in-secp256k1-today/2384/9
      // The point corresponding to the address returned by
      // ecrecover(-s*p[0],v,p[0],c*p[0]) is
      // (p[0]‚Åª¬π mod GROUP_ORDER)*(c*p[0]-(-s)*p[0]*g)=c*p+s*g.
      // See https://crypto.stackexchange.com/a/18106
      // https://bitcoin.stackexchange.com/questions/38351/ecdsa-v-r-s-what-is-v
      address computed = ecrecover(pseudoHash, v, bytes32(p[0]), pseudoSignature);
      return computed == lcWitness;
    }
  }

  // c*p1 + s*p2. Requires cp1Witness=c*p1 and sp2Witness=s*p2. Also
  // requires cp1Witness != sp2Witness (which is fine for this application,
  // since it is cryptographically impossible for them to be equal. In the
  // (cryptographically impossible) case that a prover accidentally derives
  // a proof with equal c*p1 and s*p2, they should retry with a different
  // proof nonce.) Assumes that all points are on secp256k1
  // (which is checked in verifyVRFProof below.)
  function linearCombination(
    uint256 c,
    uint256[2] memory p1,
    uint256[2] memory cp1Witness,
    uint256 s,
    uint256[2] memory p2,
    uint256[2] memory sp2Witness,
    uint256 zInv
  ) internal pure returns (uint256[2] memory) {
    unchecked {
      // Note we are relying on the wrap around here
      require((cp1Witness[0] % FIELD_SIZE) != (sp2Witness[0] % FIELD_SIZE), "points in sum must be distinct");
      require(ecmulVerify(p1, c, cp1Witness), "First mul check failed");
      require(ecmulVerify(p2, s, sp2Witness), "Second mul check failed");
      return affineECAdd(cp1Witness, sp2Witness, zInv);
    }
  }

  // Domain-separation tag for the hash taken in scalarFromCurvePoints.
  // Corresponds to scalarFromCurveHashPrefix in vrf.go
  uint256 internal constant SCALAR_FROM_CURVE_POINTS_HASH_PREFIX = 2;

  // Pseudo-random number from inputs. Matches vrf.go/scalarFromCurvePoints, and
  // https://datatracker.ietf.org/doc/html/draft-irtf-cfrg-vrf-05#section-5.4.3
  // The draft calls (in step 7, via the definition of string_to_int, in
  // https://datatracker.ietf.org/doc/html/rfc8017#section-4.2 ) for taking the
  // first hash without checking that it corresponds to a number less than the
  // group order, which will lead to a slight bias in the sample.
  //
  // TODO(alx): We could save a bit of gas by following the standard here and
  // using the compressed representation of the points, if we collated the y
  // parities into a single bytes32.
  // https://www.pivotaltracker.com/story/show/171120588
  function scalarFromCurvePoints(
    uint256[2] memory hash,
    uint256[2] memory pk,
    uint256[2] memory gamma,
    address uWitness,
    uint256[2] memory v
  ) internal pure returns (uint256 s) {
    return uint256(keccak256(abi.encodePacked(SCALAR_FROM_CURVE_POINTS_HASH_PREFIX, hash, pk, gamma, v, uWitness)));
  }

  // True if (gamma, c, s) is a correctly constructed randomness proof from pk
  // and seed. zInv must be the inverse of the third ordinate from
  // projectiveECAdd applied to cGammaWitness and sHashWitness. Corresponds to
  // section 5.3 of the IETF draft.
  //
  // TODO(alx): Since I'm only using pk in the ecrecover call, I could only pass
  // the x ordinate, and the parity of the y ordinate in the top bit of uWitness
  // (which I could make a uint256 without using any extra space.) Would save
  // about 2000 gas. https://www.pivotaltracker.com/story/show/170828567
  function verifyVRFProof(
    uint256[2] memory pk,
    uint256[2] memory gamma,
    uint256 c,
    uint256 s,
    uint256 seed,
    address uWitness,
    uint256[2] memory cGammaWitness,
    uint256[2] memory sHashWitness,
    uint256 zInv
  ) internal view {
    unchecked {
      require(isOnCurve(pk), "public key is not on curve");
      require(isOnCurve(gamma), "gamma is not on curve");
      require(isOnCurve(cGammaWitness), "cGammaWitness is not on curve");
      require(isOnCurve(sHashWitness), "sHashWitness is not on curve");
      // Step 5. of IETF draft section 5.3 (pk corresponds to 5.3's Y, and here
      // we use the address of u instead of u itself. Also, here we add the
      // terms instead of taking the difference, and in the proof construction in
      // vrf.GenerateProof, we correspondingly take the difference instead of
      // taking the sum as they do in step 7 of section 5.1.)
      require(verifyLinearCombinationWithGenerator(c, pk, s, uWitness), "addr(c*pk+s*g)!=_uWitness");
      // Step 4. of IETF draft section 5.3 (pk corresponds to Y, seed to alpha_string)
      uint256[2] memory hash = hashToCurve(pk, seed);
      // Step 6. of IETF draft section 5.3, but see note for step 5 about +/- terms
      uint256[2] memory v = linearCombination(c, gamma, cGammaWitness, s, hash, sHashWitness, zInv);
      // Steps 7. and 8. of IETF draft section 5.3
      uint256 derivedC = scalarFromCurvePoints(hash, pk, gamma, uWitness, v);
      require(c == derivedC, "invalid proof");
    }
  }

  // Domain-separation tag for the hash used as the final VRF output.
  // Corresponds to vrfRandomOutputHashPrefix in vrf.go
  uint256 internal constant VRF_RANDOM_OUTPUT_HASH_PREFIX = 3;

  struct Proof {
    uint256[2] pk;
    uint256[2] gamma;
    uint256 c;
    uint256 s;
    uint256 seed;
    address uWitness;
    uint256[2] cGammaWitness;
    uint256[2] sHashWitness;
    uint256 zInv;
  }

  /* ***************************************************************************
     * @notice Returns proof's output, if proof is valid. Otherwise reverts

     * @param proof vrf proof components
     * @param seed  seed used to generate the vrf output
     *
     * Throws if proof is invalid, otherwise:
     * @return output i.e., the random output implied by the proof
     * ***************************************************************************
     */
  function randomValueFromVRFProof(Proof memory proof, uint256 seed) internal view returns (uint256 output) {
    verifyVRFProof(
      proof.pk,
      proof.gamma,
      proof.c,
      proof.s,
      seed,
      proof.uWitness,
      proof.cGammaWitness,
      proof.sHashWitness,
      proof.zInv
    );
    output = uint256(keccak256(abi.encode(VRF_RANDOM_OUTPUT_HASH_PREFIX, proof.gamma)));
  }
}


// File contracts/Random/RandomSeedGenerator.sol

// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;






/// @title RandomSeedGenerator
/// @notice A smart contract that uses the Chainlink VRF to generate the random numbers needed to enhance an item.
contract RandomSeedGenerator is ERC2771Context, NextOwnablePausable, VRF, VRFRequester {
    struct RandomSeedGeneratorState {
        uint8 phase;
        uint64 maxDepth;
        uint64 nextReveal;
    }

    bytes32 private _keyHash;

    uint256 private _lastRequestId;

    uint256[] private _inputSeedAt;

    mapping(uint256 => uint256) private _secretSeedOf;

    RandomSeedGeneratorState private _state;

    /// @notice Emitted when requested generating a random value to VRF
    /// @param sequence Sequence of random value
    /// @param requestId Request id of VRF request
    event RandomSeedRequested(uint64 indexed sequence, uint256 requestId);
    /// @notice Emitted when fulfilled the random value from VRF
    /// @param sequence Sequence of random value
    /// @param inputSeed Random value that generated from VRF
    event RandomSeedGenerated(uint64 indexed sequence, uint256 inputSeed);
    /// @notice Emitted when revealed the random value
    /// @param sequence Sequence of random value
    /// @param secretSeed Value computed via VRF Proof
    event RandomSeedRevealed(uint64 indexed sequence, uint256 secretSeed);
    /// @notice Emitted when new key hash set
    /// @param newKeyHash Value of new key hash
    event NewKeySet(bytes32 indexed newKeyHash);

    modifier validAddress(address addr) {
        require(addr != address(0), "RandomSeedGenerator/validAddress: couldn't be zero address");
        _;
    }

    /// @dev Need CAs and values for deploy this contract
    /// @param keyHash_ Value of key hash
    /// @param maxDepth_ Count of maximum depth
    constructor(
        address trustedForwarder,
        IVRFManager vrfManager_,
        bytes32 keyHash_,
        uint64 maxDepth_
    ) validAddress(address(vrfManager_)) ERC2771Context(trustedForwarder) VRFRequester(vrfManager_) {
        require(maxDepth_ > 0, "RandomSeedGenerator/invalidMaxDepth: maxDepth must be bigger than 0");
        _keyHash = keyHash_;
        _state.maxDepth = maxDepth_;
    }

    /// @notice Request generating a random value to VRF
    function next() external whenExecutable {
        RandomSeedGeneratorState memory s = _state;

        require(s.phase == 0, "RandomSeedGenerator/randomNumberNotFulfilled: previous request not fulfilled");

        uint64 sequence = uint64(_inputSeedAt.length);
        assert(sequence < type(uint64).max);
        require(sequence - s.nextReveal < s.maxDepth, "RandomSeedGenerator/pendingRequests: too many unrevealed seeds");

        uint256 requestId = _requestVRF(1);

        _state.phase = 1;
        _lastRequestId = requestId;

        emit RandomSeedRequested(sequence, requestId);
    }

    /// @notice Reveal the random value using inforamtion of proof
    /// @param proof Information of proofing random value
    function reveal(Proof calldata proof) external {
        uint64 nextReveal = _state.nextReveal;
        uint256 secretSeed = verifyAndComputeSeed(nextReveal, proof);
        uint256 inputSeed = _inputSeedAt[nextReveal];

        _secretSeedOf[inputSeed] = secretSeed;
        _state.nextReveal = nextReveal + 1;

        emit RandomSeedRevealed(nextReveal, secretSeed);
    }

    /// @notice Set `KeyHash`
    /// @param newKeyHash Value of new key hash
    function setKeyHash(bytes32 newKeyHash) external onlyOwner {
        _keyHash = newKeyHash;

        emit NewKeySet(newKeyHash);
    }

    /// @notice Change `vrfManager`
    /// @param vrfManager Address of new VRFManager contract
    function changeVRFManager(IVRFManager vrfManager) external validAddress(address(vrfManager)) onlyOwner whenPaused {
        _changeVRFManager(vrfManager);
    }

    /// @notice Callback function called by the VRF coordinator contract to fulfill a VRF request
    /// @param requestId The ID of the VRF request being fulfilled
    /// @param randomWords An array containing the random words generated by the VRF
    function fulfillVRF(uint256 requestId, uint256[] memory randomWords) external override onlyVRFManager {
        RandomSeedGeneratorState memory s = _state;

        assert(s.phase == 1);
        assert(randomWords.length == 1);
        require(requestId == _lastRequestId, "RandomSeedGenerator/invalidRequestId: invalid request");

        uint64 sequence = uint64(_inputSeedAt.length);
        uint256 inputSeed = randomWords[0];
        assert(inputSeed != 0);

        _inputSeedAt.push(inputSeed);

        _state.phase = 0;

        emit RandomSeedGenerated(sequence, inputSeed);
    }

    /// @notice Retrieve current state
    /// @return nextRequest Value of next sequence
    /// @return nextReveal Value of the next reveal sequence
    /// @return maxDepth Current value of `maxDepth`
    function sequences() external view returns (uint64 nextRequest, uint64 nextReveal, uint64 maxDepth) {
        RandomSeedGeneratorState memory s = _state;
        nextRequest = uint64(_inputSeedAt.length);
        nextReveal = s.nextReveal;
        maxDepth = s.maxDepth;
    }

    /// @notice Retrieve `KeyHash`
    function keyHash() external view returns (bytes32) {
        return _keyHash;
    }

    /// @notice Retrieve `secretSeed` by `sequence`
    /// @param sequence Sequence of random value
    /// @return secretSeed Value computed via VRF Proof
    function secretSeedAt(uint64 sequence) external view returns (uint256 secretSeed) {
        secretSeed = secretSeedOf(inputSeedAt(sequence));
    }

    /// @notice Retrieve values of `_lastDeadline` and `_lastRequestId`
    function pendingRequest() external view returns (uint256) {
        return _lastRequestId;
    }

    /// @notice Retrieve `inputSeed` by `sequence`
    /// @param sequence Sequence of random value
    /// @return inputSeed Random value that generated from VRF
    function inputSeedAt(uint64 sequence) public view returns (uint256 inputSeed) {
        require(sequence < _inputSeedAt.length, "RandomSeedGenerator/inputSeedNotReady: input seed not yet generated");
        inputSeed = _inputSeedAt[sequence];
    }

    /// @notice Retrieve `secretSeed` by `inputSeed`
    /// @param inputSeed Random value that generated from VRF
    /// @return secretSeed Value computed via VRF Proof
    function secretSeedOf(uint256 inputSeed) public view returns (uint256 secretSeed) {
        secretSeed = _secretSeedOf[inputSeed];
        require(secretSeed != 0, "RandomSeedGenerator/secretSeedNotReady: secret seed not yet revealed");
    }

    /// @notice Calculate key hash
    /// @param publicKey Public keys for calculating key hash
    /// @return byte32 Value of key hash
    function hashOfKey(uint256[2] memory publicKey) public pure returns (bytes32) {
        return keccak256(abi.encode(publicKey));
    }

    /// @notice Verify and coumpute the `secretSeed` using `proof`
    /// @param sequence Sequence of random value
    /// @param proof Information of proofing random value
    /// @return secretSeed Value computed via VRF Proof
    function verifyAndComputeSeed(uint256 sequence, Proof calldata proof) public view returns (uint256 secretSeed) {
        require(hashOfKey(proof.pk) == _keyHash, "RandomSeedGenerator/wrongProvingKey: wrong proving key");

        require(sequence < _inputSeedAt.length, "RandomSeedGenerator/noInputSeed: no input seed");
        uint256 inputSeed = _inputSeedAt[sequence];

        secretSeed = randomValueFromVRFProof(proof, inputSeed);
        assert(secretSeed != 0);
    }

    /* trivial overrides */

    function _msgSender() internal view virtual override(Context, ERC2771Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view virtual override(Context, ERC2771Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }
}
